        success_rate = success_count / len(history.my_actions)
        if history.my_actions[-1]:
            self.cooperation_streak += 1
        else:
            self.cooperation_streak = 0
        if current_round == r:
            if success_rate >= 0.4:
                return Action.C
            elif last_round_cooperators >= m - 1:
                return Action.C
            elif historical_coop_rate >= 0.5:
                return Action.C
            else:
                return Action.D
        coop_streak_limit = 2
